import React, {useEffect, useState} from 'react';
import './App.css';
import {Counter} from "./Counter";
import {MaxMin} from "./Max&&Min";


function App() {

    const [MaxValue, SetMaxValue] = useState(5)
    const [MinValue, SetMinValue] = useState(0)
    const [editMode, SetEditMode] = useState(false)
    const [fastMax,SetFAStMAx]=useState<number>(5)
    const [fastMin,SetFAStMin]=useState<number>(0)
    const [active,SetActive]=useState(false)

    // useEffect(()=>{
    //         let GetItem=localStorage.getItem('curentValue')
    //         if(GetItem){const newValue =JSON.parse(GetItem)
    //             SetMinValue(newValue)}
    //     },[]
    // )
    //
    // useEffect(()=>{
    //         localStorage.setItem('curentValue',JSON.stringify(MinValue))
    //     },[MinValue]
    //
    // )
    // useEffect(()=>{
    //         let GetItem=localStorage.getItem('curentValue')
    //         if(GetItem){const newValue =JSON.parse(GetItem)
    //             SetMaxValue(newValue)}
    //     },[]
    // )

    useEffect(()=>{
            localStorage.setItem('currentValue',JSON.stringify(MaxValue))
        },[MaxValue]

    )

    const Inc = () => {
        SetMinValue(prevState => prevState + 1)
    }
    const Reset = () => {
        SetMinValue(0)
    }

    const KeyDown = () => {
        SetEditMode(!editMode)
    }
    const Set = (startValue: number, MaxValue: number) => {
        SetMinValue(startValue)
        SetMaxValue(MaxValue)
        SetActive(false)

    }

    const FastMaxValue =(e:number)=> {
        SetFAStMAx(e)
        SetActive(true)
    }
    const FastMinValue =(e:number)=> {
        SetFAStMin(e)
        SetActive(true)
    }
    return (
        <div className="App">

            <Counter inc={Inc} reset={Reset} disable={MaxValue} StartValue={MinValue} editMode={editMode} fastMax={fastMax} fastMin={fastMin} active={active}/>
            <MaxMin disable={MaxValue} StartValue={MinValue} keyDown={KeyDown} set={Set} FastMaxValue={FastMaxValue} FastMinValue={FastMinValue}/>

        </div>
    );
}

export default App;
