import React, {ChangeEvent, useEffect, useState} from 'react';
import s from "./Max&&Min.module.css"

type  MaxMinType ={
    disable:number
    StartValue:number
    set:(startValue:number,MaxValue:number)=>void
    FastMaxValue:(e:number)=>void
    FastMinValue:(e:number)=>void
}


export const MaxMin = (props:MaxMinType) => {

   const [startValue,SetStartValue] =useState<number>(0)
    const [MaxValue,SetMaxValue] =useState<number>(5)

    useEffect(()=>{
            let GetItem=localStorage.getItem('min')
            if(GetItem){const newValue =JSON.parse(GetItem)
                SetStartValue(newValue)}
        },[]
    )


    useEffect(()=>{
            localStorage.setItem('min',JSON.stringify(startValue))
        },[startValue]

    )


    const MaxValueChange =(e:ChangeEvent<HTMLInputElement>)=> {
       SetMaxValue(+e.currentTarget.value)
        props.FastMaxValue(+e.currentTarget.value)

    }
    const MinValueChange =(e:ChangeEvent<HTMLInputElement>)=> {
        SetStartValue(+e.currentTarget.value)
        props.FastMinValue(+e.currentTarget.value)
    }

    const Set =()=> {
       props.set(startValue,MaxValue)
    }
    return (
        <div className={s.around_wrapper}>
            <div className={s.score}>
                <div> Max Value:
                    <input className={startValue>=MaxValue ||  MaxValue<0 ?s.error :''}  type='number' value={MaxValue} onChange={MaxValueChange} />
                </div>

                <div> Min Value:
                    <input className={startValue>=MaxValue ||  startValue<0 ?s.error :''} type='number' value={startValue} onChange={MinValueChange}  />
                </div>

            </div>

            <button onClick={Set} disabled={startValue>=MaxValue || startValue<0 || MaxValue<0}>set</button>
        </div>
    );
};
