import React, {ChangeEvent, useState} from 'react';
import s from './MaxValue.module.css'


type CounterMaxValueType ={
    addSet:()=>void
    MinValueChange:(e:number)=>void
    MaxValueChange:(e:number)=>void
    counter:number
    disable:number
}



export const CounterMaxValue = (props:CounterMaxValueType) => {





    const Set =()=> {
       props.addSet()
    }


const [error,SetError]=useState(false)

    const MaxValueChange =(e:ChangeEvent<HTMLInputElement>)=> {
        props.MaxValueChange(+e.currentTarget.value)
    }

    const MinValueChange =(e:ChangeEvent<HTMLInputElement>)=> {
        props.MinValueChange(+e.currentTarget.value)


    }

       const onBlur =()=> {
    if(props.counter>props.disable){SetError(!error)}
}
    return (
        <div className={s.fon}>
            <div className={s.around_wrapper}>
                <div className={s.around}>
                    <div> Max Value
                        <input onChange={MaxValueChange} />

                    </div>
                    <div>
                        Min Value
                        <input onChange={MinValueChange} onBlur={onBlur} disabled={error} className={error ?s.error_Score : ''} />

                    </div>

             <button onClick={Set}>set</button>
                </div>



            </div>

        </div>
    );
};
