import React, {ChangeEvent, DetailedHTMLProps, KeyboardEvent, useState} from 'react';
import s from './MaxValue.module.css'
import {isDisabled} from "@testing-library/user-event/dist/utils";


type CounterMaxValueType ={
    addSet:()=>void
    MinValueChange:(e:number)=>void
    MaxValueChange:(e:number)=>void
    counter:number
    disable:number
}



export const CounterMaxValue = (props:CounterMaxValueType) => {





    const Set =()=> {
       props.addSet()

    }


const [error,SetError]=useState(false)

    const MaxValueChange =(e:number)=> {
        if(props.counter>=props.disable){SetError(!error)}
        if(props.counter<0){SetError(!error)}
        else (props.MaxValueChange(e))
    }

    const MinValueChange =(e:ChangeEvent<HTMLInputElement>)=> {
        if(props.counter>=props.disable){SetError(!error)}
        if(props.counter<0){SetError(!error)}
        else ( props.MinValueChange(+e.currentTarget.value))



    }

       const onBlur =()=> {



}

    const onKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
       if ( e.key==='Enter'){SetError(false)}
        else (MaxValueChange())

    }

    // const onKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    //     if (e.key === 'Enter') {
    //         AddMessage()
    //     } else (SetError(''))
    // }



    return (
        <div className={s.fon}>
            <div className={s.around_wrapper}>
                <div className={s.around}>
                    <div> Max Value
                        <input onChange={(e)=>MaxValueChange(+e.currentTarget.value)}  type="number" disabled={error} className={error ? s.error_Score : ''} onKeyDown={onKeyDown}  autoFocus   />

                    </div>
                    <div>
                        Min Value
                        <input onChange={MinValueChange}  disabled={error} className={error ? s.error_Score : ''} type="number"   onKeyDown={onKeyDown} autoFocus />

                    </div>

             <button onClick={Set} disabled={error}>set</button>
                </div>



            </div>

        </div>
    );
};
