import React, {useState} from 'react';
import './App.css';
import {Counter} from "./Counter";
import {CounterMaxValue} from "./CounterMaxValue";


function App() {
    const [set,Setset]=useState(false)
    const [counter,SetCounter]=useState(0)
    const [disable,SetDisable]=useState(5)

    const Set =(max:number,min:number)=> {
        Setset(!set)
        SetDisable(max)
        SetCounter(min)


    }

    const AddSet =()=> {
        Setset(!set)
    }



    const Reset = () => {
      SetCounter(0)
    }

    const AddCounter =()=> {
        SetCounter((prevState) => ++ prevState )
    }


    return (
        <div className="App">{set ===false
            ?
            <Counter addSet={AddSet} reset={Reset} addCounter={AddCounter} counter={counter} disable={disable} /> :
            <CounterMaxValue addSet={Set}   counter={counter} disable={disable} />
        }


        </div>
    );
}

export default App;
